import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Stat from "./Stat";

export default function StatisticsPage({ userId }) {
  const [isLoaded, setIsLoaded] = useState("");

  useEffect(() => {
    doFetch();
  }, []);

  const doFetch = () => {
    fetch("http://localhost:5000", {
      method: "post",
      headers: {
        //"Content-Type": "application/json",
        //"Access-Control-Allow-Credentials": true,
      },
      //credentials: "include",
      body: JSON.stringify({
        requestType: "getUserStatistics",
        userId: userId,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          //If there was an error fetching the data
          if (result.response.apiStatusCode !== "OK") {
            setIsLoaded("error");
            setIsLoggedIn(false);
            return;
          }
          //If the user is not logged in
          if (result.response.data.loggedIn === 0) {
            setIsLoggedIn(false);
            //setIsLoaded("incorrect");
            return;
          }
          //The user is logged in
          if (result.response.data.loggedIn === 1) {
            setIsLoggedIn(true);
            setUserId(result.response.data.dealerid);
            setUserName(result.response.data.dealerName);
            setIsLoaded("loaded");
          }
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded("error");
          //console.log(error);
        }
      );
  };

  return (
    <PageWrapper>
      <div>
        <Stat />
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav``;
