import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useTable, useSortBy } from "react-table";

let fakeData = [
  {
    item: "AnItem",
    embelishments: "fire",
    price: "$30.50",
  },
  {
    item: "AnotherItem",
    embelishments: "none",
    price: "$0.70",
  },
  {
    item: "LastItem",
    embelishments: "ice",
    price: "$100000.90",
  },
];
export default function GenerateBox({ typeBox, tables }) {
  const [nextGenerated, setNextGenerated] = useState("");
  const [counter, setCounter] = useState(0);
  const [isLoaded, setIsLoaded] = useState("");
  const [randResult, setRandResult] = useState("");

  const handleClick = (e) => {
    e.preventDefault();
    setCounter(counter + 1);
    setNextGenerated(counter);

    //Here is where you will make a call to the backend for information
    //Pass it the tables to include, and other filters
    //doFetch(typeBox, tables);
    setRandResult(fakeData);
  };

  const doFetch = (tablesToInclude) => {
    fetch("http://localhost:5000/req/generate", {
      method: "post",
      headers: {
        //"Content-Type": "application/json",
        //"Access-Control-Allow-Credentials": true,
      },
      //credentials: "include",
      body: JSON.stringify({
        tables: tablesToInclude,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          //If there was an error fetching the data
          if (result.response.apiStatusCode !== "OK") {
            setIsLoaded("error");
            return;
          } else {
            setRandResult(result.response);
            setIsLoaded("loaded");
          }
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded("error");
          //console.log(error);
        }
      );
  };

  const columns = React.useMemo(
    () => [
      {
        Header: "Demo ID",
        accessor: "item",
      },
      {
        Header: "Dealer Number",
        accessor: "embelishments",
      },
      {
        Header: "Salesperson",
        accessor: "price",
      },
    ],
    []
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable(
      {
        columns,
        data: fakeData,
      },
      useSortBy
    );

  const handleRowClick = (e) => {
    //do something
  };

  return (
    <PageWrapper>
      <div>
        <form className="generate-form">
          <table {...getTableProps()}>
            <thead>
              {headerGroups.map((headerGroup) => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map((column) => (
                    <th
                      {...column.getHeaderProps(column.getSortByToggleProps())}
                    >
                      {column.render("Header")}
                      <span>
                        {column.isSorted
                          ? column.isSortedDesc
                            ? "▼"
                            : "▲"
                          : " ▲▼"}
                      </span>
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {rows.map((row, i) => {
                prepareRow(row);
                return (
                  <tr
                    {...row.getRowProps()}
                    key={i}
                    onClick={() => handleRowClick(row)}
                  >
                    {row.cells.map((cell) => {
                      return (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
          </table>
          <input readOnly={true} value={nextGenerated} className="textBox" />
          <button className="mainBtn" onClick={handleClick}>
            Generate!
          </button>
        </form>
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav`
  display: flex;
  flex-direction: column;

  .generate-form {
    display: flex;
    flex-direction: column;
    margin-bottom: 3px;
  }
  .textBox {
    margin-bottom: 5px;
  }
  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }
    tr:hover {
      th {
        background: var(--mainWhite);
      }
      background: var(--lightGrey);
    }

    th,
    td {
      margin: 0;
      padding: 0.15rem 0.15rem 0.15rem 0.35rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }
    }
  }
`;
