import React, { useState, useEffect } from "react";
import styled from "styled-components";

export default function GenerateBox({ typeBox, tables }) {
  const [nextGenerated, setNextGenerated] = useState("");
  const [counter, setCounter] = useState(0);
  const [isLoaded, setIsLoaded] = useState("");

  const handleClick = (e) => {
    e.preventDefault();
    setCounter(counter + 1);
    setNextGenerated(counter);

    //Here is where you will make a call to the backend for information
    //Pass it the tables to include, and other filters
  };

  const doFetch = (e) => {
    fetch("http://localhost:5000/req/generate", {
      method: "post",
      headers: {
        //"Content-Type": "application/json",
        //"Access-Control-Allow-Credentials": true,
      },
      //credentials: "include",
      body: JSON.stringify({
        tables: tables,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          //If there was an error fetching the data
          if (result.response.apiStatusCode !== "OK") {
            setIsLoaded("error");
            setIsLoggedIn(false);
            return;
          }
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded("error");
          //console.log(error);
        }
      );
  };

  return (
    <PageWrapper>
      <div>
        <form className="generate-form">
          <input readOnly={true} value={nextGenerated} className="textBox" />
          <button className="mainBtn" onClick={handleClick}>
            Generate!
          </button>
        </form>
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav`
  display: flex;
  flex-direction: column;

  .generate-form {
    display: flex;
    flex-direction: column;
    margin-bottom: 3px;
  }
  .textBox {
    margin-bottom: 5px;
  }
`;
