import React, { useState, useEffect } from "react";
import styled from "styled-components";
import CreateTable from "./CreateTable";
import DisplayAndEditTables from "./DisplayAndEditTables";

let starterData = {
  name: "",
  unitPrice: "",
  baseWeight: "",
  weightType: "",
  quantityMin: "",
  quantityMax: "",
  description: "",
  relativeChance: "",
};

export default function AddItems({ tables }) {
  const [tableData, setTableData] = useState("");
  const [addIsClicked, setAddIsClicked] = useState(false);
  const [newItemData, setNewItemData] = useState(starterData);
  const [goIsClicked, setGoIsClicked] = useState(false);

  useEffect(() => {
    setTableData(AddCheckedProperty(tables));
  });

  const AddCheckedProperty = (tables) => {
    let finishedArray = [];
    tables.map((item) => {
      let curItem = {
        name: item.name,
        id: item.id,
        checked: false,
      };
      finishedArray.push(curItem);
    });
    return finishedArray;
  };

  const handleNewItemClick = (e) => {
    e.preventDefault();
    setAddIsClicked(true);
  };

  const TablesCheckboxes = () => {
    const handleToggle = (e, index) => {
      let item = tableData[index];
      //item.endusetypecode = 99;
      //console.log(item);
      //item.active = e.target.value;
      item.checked = !item.checked;
      e.target.checked = item.checked;
      e.target.value = item.checked;

      let newState = tableData
        .slice(0, index)
        .concat(item)
        .concat(tableData.slice(index + 1));

      setTableData(newState);
      //handleCheckChange(newState);

      //console.log(checkedData);
    };
    // setState(s => ({ ...s, [target.name]:!s[target.name]}));

    let checkboxes = tableData.map((item, idx) => {
      let chkstr = "";
      //console.log("ITEM: " + item);
      return (
        <div className="checkboxes">
          <label className="checkboxes">
            <input
              type="radio"
              //onClick={(e) => handleToggle(e, idx)}
              //onChange={handleChange}
              key={item.id}
              name={item.name}
              value={item.name}
              checked={item.checked}
              onChange={(e) => handleToggle(e, idx)}
              className="checkboxes"
              readOnly={true}
            />
            {item.name}
          </label>
        </div>
      );
    });
    return <div>{checkboxes}</div>;
  };

  const handleInputChange = (e) => {
    setNewItemData({
      ...newItemData,
      [e.target.name]: e.target.value,
    });
  };

  const handleNewItemAddClick = (e) => {
    e.preventDefault();
    console.log(e.target);
  };

  const handleGoClick = (e) => {
    e.preventDefault();
    setGoIsClicked(true);
    setAddIsClicked(false);
  };

  return (
    <PageWrapper>
      <div>
        {!goIsClicked && (
          <button className="mainBtn" onClick={handleNewItemClick}>
            Add New Item
          </button>
        )}

        {addIsClicked && (
          <div>
            <div>Please select the table to add an item to.</div>
            <TablesCheckboxes />
            <button className="mainBtn" onClick={handleGoClick}>
              Go!
            </button>
          </div>
        )}
        {goIsClicked && (
          <div>
            <form>
              <input
                className="formInput"
                name="itemNameInput"
                placeholder="Item Name"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="unitPriceInput"
                placeholder="Item Unit Price"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="baseWeightInput"
                placeholder="Item Base Weight"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="weightTypeInput"
                placeholder="Item Weight Type"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="quantityMinInput"
                placeholder="Item Quantity Min"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="quantityMaxInput"
                placeholder="Item Quantity Max"
                size="12"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="descriptionInput"
                placeholder="Item Description Name"
                size="20"
                maxLength={20}
                onChange={handleInputChange}
              />
              <input
                className="formInput"
                name="relativeChanceInput"
                placeholder="Item Relative Chance"
                size="20"
                maxLength={20}
                onChange={handleInputChange}
              />
              <button className="mainBtn" onClick={handleNewItemAddClick}>
                Add
              </button>
            </form>
          </div>
        )}
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav`
  .separaterBottom {
    margin-bottom: 20px;
  }
`;
