import { useState, useEffect } from "react";
import styled from "styled-components";
import { Route, Switch } from "react-router";
import OriginalGeneratorPage from "./components/generatorPages/OriginalGeneratorPage";
import SpicyGeneratorPage from "./components/generatorPages/SpicyGeneratorPage";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import TableInfoPage from "./components/tableInfoPage/TableInfoPage";
import { Fragment } from "react/cjs/react.production.min";
import "./App.css";
import LoginPage from "./components/loginSignUpPages/LoginPage";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [userName, setUserName] = useState("");
  const [loaded, setIsLoaded] = useState(null);

  useEffect(() => {
    //fetchUserData();

    //The following is to simulate a call to fetchUserData()
    setIsLoggedIn(true);
    setIsLoaded("loaded");
    setUserName("sldiener");
  }, [isLoggedIn]);

  if (!isLoggedIn) {
    //Adds the login page class to root so the login page is centered
    var element = document.getElementById("root");
    element.className = element.className.replace(
      /\bregularPage\b/g,
      "loginPage"
    );
    return (
      <LoginPageWrapper>
        <div>
          <LoginPage setIsLoggedIn={setIsLoggedIn} />
          {isLoaded === "error" && (
            <div className="errorMessage">An unkown error occured.</div>
          )}
        </div>
      </LoginPageWrapper>
    );
  }

  return (
    <PageWrapper>
      <div>
        <Header userName={userName} isLoggedIn={isLoggedIn} />
        <div className="contentWrapper">
          <Sidebar isLoggedIn={isLoggedIn} />
          <Switch>
            <Route exact path="/" component={OriginalGeneratorPage} />
            {isLoggedIn && (
              <Fragment>
                <Route path="/spicygenerator" component={SpicyGeneratorPage} />
                <Route path="/tableinfo" component={TableInfoPage} />
              </Fragment>
            )}
          </Switch>
        </div>
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav`
  display: flex;

  .contentWrapper {
    display: flex;
    flex-direction: row;
    align-items: left;
  }
`;

export default App;
