import React from "react";
import styled from "styled-components";
import ExtrasCheckboxes from "./FilterSections/ExtrasCheckboxes";
import ItemCategoryCheckboxes from "./FilterSections/ItemCategoryCheckboxes";

export default function Filter({
  itemCategoryData,
  setItemCategoryData,
  extrasData,
  setExtrasData,
}) {
  return (
    <PageWrapper>
      <div>
        <div className="filtersHeader">Filters</div>
        <div className="sectionHeader">General Filters</div>
        <ItemCategoryCheckboxes data={extrasData} setData={setExtrasData} />
        <div className="sectionHeader">Item Category Filters</div>
        <ItemCategoryCheckboxes
          data={itemCategoryData}
          setData={setItemCategoryData}
        />
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav`
  display: flex;
  flex-direction: column;
  border: 3px solid black;
  .filtersHeader {
    font-size: 2rem;
  }
  .sectionHeader {
    font-size: 1.5rem;
  }
`;
