import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Stat from "./Stat";

export default function StatisticsPage({ userId }) {
  const [isLoaded, setIsLoaded] = useState("");
  const [numberOfTables, setNumberOfTables] = useState();
  const [numberOfItems, setNumberOfItems] = useState();
  const [joinedOn, setJoinedOn] = useState();

  useEffect(() => {
    doFetch();
  }, []);

  const doFetch = () => {
    fetch("http://localhost:5000", {
      method: "post",
      headers: {
        //"Content-Type": "application/json",
        //"Access-Control-Allow-Credentials": true,
      },
      //credentials: "include",
      body: JSON.stringify({
        requestType: "getUserStatistics",
        userId: userId,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          //If there was an error fetching the data
          if (result.response.apiStatusCode !== "OK") {
            setIsLoaded("error");
            return;
          }
          setJoinedOn(result.joinedOn);
          setNumberOfTables(result.numberOfTables);
          setNumberOfItems(result.numberOfItems);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded("error");
          //console.log(error);
        }
      );
  };

  return (
    <PageWrapper>
      <div>
        <Stat />
      </div>
    </PageWrapper>
  );
}

const PageWrapper = styled.nav``;
